    public bool VerifyNumericVal(string number)
    {
        string expresion;
        expresion = "^[\\d]{10,10}";
        if (Regex.IsMatch(number, expresion))
        {
            if (Regex.Replace(number, expresion, string.Empty).Length == 0)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        else
        {
            return false;
        }
    }
